mapper.select:
SELECT user.id
FROM user
WHERE user.id is not NULL
AND user.deleted = #{deleted}
AND user.username LIKE CONCAT('%', #{username}, '%')

    AND #{createdAt} > user.created_at
    ORDER BY user.created_at DESC


    AND user.created_at > #{createdAt}
    ORDER BY user.created_at ASC

LIMIT 0, #{perpage}
